mapper.select:
select no, name, seilling_price as seillingPrice, concat(left(g.viewdetail,30),'...') as detail, (select name from tblSmallCategory where no = g.smallcategory_no) as smallcategoryName, (select name from tblBigCategory where no =(select bigcategory_no from tblSmallCategory where no = g.smallcategory_no)) as bigcategoryName, seilling_price as seillingPrice, (select image from tblGoodsImages where goods_no=g.no and ismain='y') as thumbnail from tblGoods g where display_status = 'y' and isdel is null
and no < #{_parameter}
order by no desc limit 0,8
